$(document).ready(function(){function t(t){e=new google.maps.Geocoder;var n=t.coords.latitude,r=t.coords.longitude;localStorage.clear();localStorage.setItem("latitude",n);localStorage.setItem("longitude",r);var i=new google.maps.LatLng(n,r);e.geocode({location:i},function(e,t){if(t==google.maps.GeocoderStatus.OK){var n=e[0].formatted_address;$("#your-location").html("Your location is: "+n);$("#location-next-step").show()}else $("#your-location").html("Sorry, we couldn't process your address, please try again.")})}function n(){$("#your-location").html("Failed to retreive your location.")}var e;$("#get-user-location").click(function(){navigator.geolocation?navigator.geolocation.getCurrentPosition(t,n):$("#your-location").html("Sorry, your browser does not support geolocation services.")});$("#submit").click(function(){if($("#zip-code").val().length===0||$("#zip-code").val().match(/[a-z]/gi)||$("#zip-code").val().match(/\s/gi)||$("#zip-code").val().length<5)$("#your-location").html("Invalid Zip Code.");else{localStorage.clear();var t=$("#zip-code").val();localStorage.setItem("zip",t);e=new google.maps.Geocoder;e.geocode({address:t},function(e,t){if(t==google.maps.GeocoderStatus.OK){var n=e[0].formatted_address;$("#your-location").html("Your location is: "+n);$("#location-next-step").show()}else $("#your-location").html("Sorry, we couldn't process your address, please try again.")})}})});